$(document).ready(function(){$("#faq_accordion").accordion({header:"h3"});$(window).resize(function(){var slides_width=$("#slides").css("width");$("#slides ul li").css("width",slides_width)});var step1=$("li.step1");var step2=$("li.step2");var step3=$("li.step3");arSteps=[step1,step2,step3];var slide1=$("li.slide1");var slide2=$("li.slide2");var slide3=$("li.slide3");arSlides=[slide1,slide2,slide3];showFirst();for(var i=0;i<arSteps.length;i++){(function(slide,step){step.click(function(){if(step!=step1){slide.delay(500).fadeIn("slow",showSelected(step))}else{hideAll(1);showSelected(step1)}})})(arSlides[i],arSteps[i])}function showFirst(){slide1.fadeIn("slow",showSelected(step1));hideAll(1)}function hideAll(offset){for(var i=offset;i<arSlides.length;i++){(function(slide){slide.fadeOut("slow")})(arSlides[i])}}function showSelected(selected){for(var i=0;i<arSteps.length;i++){(function(step){if(selected==step){step.addClass("selected")}else{step.removeClass("selected")}})(arSteps[i])}}});